  Widget createItemHero(Book book) {
    return Hero(
      tag: book.name,
      child: Material(
        elevation: 16,
        shadowColor: Colors.yellow,
        child: InkWell(
          onTap: () {
            Navigator.push(
              context,
              MaterialPageRoute(
                builder: (context) => BookDetail(book: book),
              ),
            );
          },
          child: Image.asset(
            book.image,
            fit: BoxFit.cover,
          ),
        ),
      ),
    );
  }

  @override
  Widget build(BuildContext context) {
    CustomScrollView customScrollView = new CustomScrollView(
      primary: false,
      slivers: <Widget>[
        SliverPadding(
          padding: EdgeInsets.all(10),
          sliver: SliverGrid.count(
            crossAxisCount: 3,
            mainAxisSpacing: 10,
            crossAxisSpacing: 10,
            childAspectRatio: 2 / 3,
            children: list.map((b) {
              return createItemHero(b);
            }).toList(),
          ),
        )
      ],
    );

    return Container(child: customScrollView,color: Colors.yellow,);
  }
}
